package com.mobility.hack;import android.content.Intent;import android.os.Bundle;import android.view.Menu;import android.view.MenuItem;import android.widget.Toast;import androidx.appcompat.app.AppCompatActivity;import androidx.appcompat.widget.Toolbar;import com.mobility.hack.auth.CheckPasswordActivity;import com.mobility.hack.auth.LoginActivity;import com.mobility.hack.security.SecurityEngine;import com.mobility.hack.security.TokenManager;public class MainActivity extends AppCompatActivity {    private TokenManager tokenManager;    @Override    protected void onCreate(Bundle savedInstanceState) {        super.onCreate(savedInstanceState);        tokenManager = new TokenManager(getApplicationContext());        if (tokenManager.fetchAuthToken() == null) {            Toast.makeText(this, "로그인이 필요합니다. 로그인 페이지로 이동합니다.", Toast.LENGTH_SHORT).show();            Intent intent = new Intent(this, LoginActivity.class);            intent.addFlags(Intent.FLAG_ACTIVITY_CLEAR_TOP | Intent.FLAG_ACTIVITY_NEW_TASK);            startActivity(intent);            finish();            return;        }        setContentView(R.layout.activity_main);        Toolbar toolbar = findViewById(R.id.toolbar);        setSupportActionBar(toolbar);        SecurityEngine engine = new SecurityEngine();        engine.initAntiDebug();    }    @Override    public boolean onCreateOptionsMenu(Menu menu) {        getMenuInflater().inflate(R.menu.main_menu, menu);        return true;    }    @Override    public boolean onOptionsItemSelected(MenuItem item) {        int id = item.getItemId();        if (id == R.id.action_my_info) {            Intent intent = new Intent(this, CheckPasswordActivity.class);            startActivity(intent);            return true;        } else if (id == R.id.action_logout) {            tokenManager.clearData();            Intent intent = new Intent(this, LoginActivity.class);            intent.addFlags(Intent.FLAG_ACTIVITY_CLEAR_TOP | Intent.FLAG_ACTIVITY_NEW_TASK);            startActivity(intent);            finish();            return true;        }        return super.onOptionsItemSelected(item);    }}